mapper.select:
select

from cere_shop_order
where order_id = #{orderId,jdbcType=BIGINT}

SELECT a.order_id,a.order_formid,a.price,IF(a.order_id IS NOT NULL,COUNT(b.product_id),NULL) number,a.state, a.customer_name,a.create_time,a.receive_name,a.receive_phone,a.address,a.receive_adress,c.shop_name FROM cere_shop_order a LEFT JOIN cere_order_product b ON a.order_id=b.order_id LEFT JOIN cere_platform_shop c ON a.shop_id=c.shop_id where a.shop_id=#{shopId}
and a.order_id like concat('%',#{search},'%')
and a.customer_name like concat('%',#{search},'%')
and a.receive_name like concat('%',#{search},'%')
and a.receive_phone like concat('%',#{search},'%')
and b.product_id like concat('%',#{search},'%')
and a.state=#{state}
and a.order_id NOT IN (SELECT order_id FROM cere_order_after)
and a.after_state=#{state}
and a.create_time>=#{startTime} and a.create_time<=#{endTime}
GROUP BY a.order_id ORDER BY
a.create_time
a.create_time DESC

SELECT sku_name, sku_value
FROM cere_order_product_attribute
where order_product_id = #{orderProductId}

SELECT withdrawal_money,state,bank_card,bank_name,apply_time from cere_shop_withdrawal
where shop_id=#{shopId}

    and apply_time>=#{startTime} and apply_time<=#{endTime}

SELECT IF(SUM(price) IS NULL, 0, SUM(price))
FROM cere_shop_order
where shop_id = #{shopId}
  and state in (2, 3)
  and create_time <= #{time}

SELECT IF(SUM(withdrawal_money) IS NULL, 0, SUM(withdrawal_money))
FROM cere_shop_withdrawal
where shop_id = #{shopId}
  and state = 0
  and apply_time <= #{time}

SELECT IF(SUM(price) IS NULL, 0, SUM(price))
FROM cere_shop_order
where shop_id = #{shopId}
  and state = 4
  and create_time <= #{time}

SELECT IF(SUM(withdrawal_money) IS NULL, 0, SUM(withdrawal_money))
FROM cere_shop_withdrawal
where shop_id = #{shopId}
  and state = 1
  and apply_time <= #{time}

